mapper.delete:
delete from sf_user_turn_table_item
where id = #{id,jdbcType=BIGINT}
  and turn_table_id = #{turnTableId,jdbcType=INTEGER}
  and user_turn_table_id = #{userTurnTableId,jdbcType=BIGINT}
  and turn_table_rule_id = #{turnTableRuleId,jdbcType=INTEGER}
  and type = #{type,jdbcType=INTEGER}
  and remark = #{remark,jdbcType=VARCHAR}
  and create_time = #{createTime,jdbcType=TIMESTAMP}

mapper.update:
update sf_user_turn_table_item
set times = #{times,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}
  and turn_table_id = #{turnTableId,jdbcType=INTEGER}
  and user_turn_table_id = #{userTurnTableId,jdbcType=BIGINT}
  and turn_table_rule_id = #{turnTableRuleId,jdbcType=INTEGER}
  and type = #{type,jdbcType=INTEGER}
  and remark = #{remark,jdbcType=VARCHAR}
  and create_time = #{createTime,jdbcType=TIMESTAMP}

mapper.select:
select id, turn_table_id, user_turn_table_id, turn_table_rule_id, type, remark, create_time, 
times
from sf_user_turn_table_item
where id = #{id,jdbcType=BIGINT}
  and turn_table_id = #{turnTableId,jdbcType=INTEGER}
  and user_turn_table_id = #{userTurnTableId,jdbcType=BIGINT}
  and turn_table_rule_id = #{turnTableRuleId,jdbcType=INTEGER}
  and type = #{type,jdbcType=INTEGER}
  and remark = #{remark,jdbcType=VARCHAR}
  and create_time = #{createTime,jdbcType=TIMESTAMP}
