    #{explanation,jdbcType=VARBINARY}, #{shoutNum,jdbcType=BIGINT}, #{pageviews,jdbcType=BIGINT}, 
    #{saleAmount,jdbcType=DECIMAL}, #{qrCode,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
    )
