mapper.select:
select id, create_time, shop_id, user_id, user_pid, tree_code, tree_level, is_buy, remark
from sf_user_relation
where user_id = #{userId,jdbcType=BIGINT}

select id, create_time, shop_id, user_id, user_pid, tree_code, tree_level, is_buy, remark
from sf_user_relation
where user_id = #{userId}
and shop_id = #{shopId}

select id, create_time, shop_id, user_id, user_pid, tree_code, tree_level, is_buy, remark
from sf_user_relation
where user_pid = #{userPid,jdbcType=BIGINT}

        select * from (
          select * from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT}
          union all select * from sf_user_relation where user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT})
	      union all select * from sf_user_relation where user_pid in (select user_id from sf_user_relation where user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT}))
        )t

select count(*) as num from sf_user_relation r inner join com_user u on r.user_id = u.id where
tree_code like CONCAT(#{treeCode},'%')

    and r.shop_id = #{shopId}

select count(*) as num from sf_user_relation r inner join com_user u on r.user_id = u.id
where
r.is_spokesman = 1

    and r.shop_id = #{shopId}

and tree_code like CONCAT(#{treeCode},'%')
and u.id <> #{userId}

select id, create_time, shop_id, user_id, user_pid, tree_code, tree_level, is_buy, remark
from sf_user_relation
where user_id = #{userId} and shop_id = #{shopId}

select * from (
select u.wx_nk_name as wxName,u.wx_head_img as headImg,LPAD(u.id,10,0) as ID,r.is_spokesman as sopkenMan,r.create_time as createTime,u.id as userId,1 as userLevel,r.shop_id as shopId,r.is_buy as isBuy
from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid = #{userPid,jdbcType=BIGINT}
union all
select u.wx_nk_name as wxName,u.wx_head_img as headImg,LPAD(u.id,10,0) as ID,r.is_spokesman as sopkenMan,r.create_time as createTime,u.id as userId, 2 as userLevel,r.shop_id as shopId,r.is_buy as isBuy
from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT})
union all
select u.wx_nk_name as wxName,u.wx_head_img as headImg,LPAD(u.id,10,0) as ID,r.is_spokesman as sopkenMan,r.create_time as createTime,u.id as userId, 3 as userLevel,r.shop_id as shopId,r.is_buy as isBuy
from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid in (select user_id from sf_user_relation where user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT}))
)t where 1=1

    and t.userLevel = #{userLevel}


    and t.shopId = #{shopId}

order by t.createTime desc

select * from (
select u.wx_nk_name as wxName,u.wx_head_img as headImg,LPAD(u.id,10,0) as ID,r.is_spokesman as sopkenMan,r.create_time as createTime,u.id as userId,1 as userLevel,r.shop_id as shopId,r.is_buy as isBuy
from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid = #{userPid,jdbcType=BIGINT}
union all
select u.wx_nk_name as wxName,u.wx_head_img as headImg,LPAD(u.id,10,0) as ID,r.is_spokesman as sopkenMan,r.create_time as createTime,u.id as userId, 2 as userLevel,r.shop_id as shopId,r.is_buy as isBuy
from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT})
)t where t.sopkenMan = #{spokenMan}

    and t.userLevel = #{userLevel}


    and t.shopId = #{shopId}

order by t.createTime desc

SELECT u.wx_nk_name AS wxName, u.wx_head_img AS headImg, LPAD(u.id, 10, 0) AS ID, r.is_spokesman AS sopkenMan, r.create_time AS createTime, u.id AS userId, 1 AS userLevel, r.shop_id AS shopId, r.is_buy AS isBuy FROM sf_user_relation r INNER JOIN com_user u ON r.user_id = u.id WHERE r.shop_id = #{shopId}
AND r.is_spokesman = #{spokesMan}
ORDER BY r.create_time desc

select count(*) as num,userLevel from (
select 1 as userLevel,r.shop_id as shopId from sf_user_relation r where r.user_pid = #{userPid,jdbcType=BIGINT}
union all select 2 as userLevel,r.shop_id as shopId from sf_user_relation r where r.user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT})
union all select 3 as userLevel,r.shop_id as shopId from sf_user_relation r where r.user_pid in (select user_id from sf_user_relation where user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT}))
)t where 1=1

    and t.shopId = #{shopId}


    and t.userLevel = #{userLevel}

group by t.userLevel

select count(*) as num,userLevel from (
select 1 as userLevel,r.is_spokesman,r.shop_id as shopId from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid = #{userPid,jdbcType=BIGINT}
union all select 2 as userLevel,r.is_spokesman,r.shop_id as shopId from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT})
union all select 3 as userLevel,r.is_spokesman,r.shop_id as shopId from sf_user_relation r inner join com_user u on r.user_id = u.id where r.user_pid in (select user_id from sf_user_relation where user_pid in (select user_id from sf_user_relation where user_pid = #{userPid,jdbcType=BIGINT}))
)t where t.is_spokesman = 1

    and t.shopId = #{shopId}


    and t.userLevel = #{userLevel}

group by t.userLevel

select id, create_time, shop_id, user_id, user_pid, tree_code, tree_level, is_buy, remark
from sf_user_relation
where
  shop_id = #{shopId}
AND
  user_id != #{userId,jdbcType=BIGINT}
