mapper.select:
select m.id, m.create_time, m.create_man, m.material_library_id, m.material_group_id, m.title, m.content, m.sort, m.modify_time, m.modify_man, m.remark,
mi.id mi_id, mi.create_time mi_create_time, mi.create_man mi_create_man, mi.material_library_id mi_material_library_id, mi.material_group_id mi_material_group_id, mi.material_id mi_material_id, mi.type mi_type, mi.file_name mi_file_name, mi.file_url mi_file_url, mi.file_suffix mi_file_suffix, mi.file_size mi_file_size, mi.sort mi_sort, mi.remark mi_remark from com_sku_material m LEFT JOIN com_sku_material_item mi ON mi.material_id = m.id
AND m.id = #{mId}
AND m.material_group_id = #{mgId}
