mapper.select:
select id, create_time, create_man, order_code, user_id, user_pid, user_message, supplier_id, modify_time, modify_man, receivable_amount, order_amount, bail_amount, product_amount, recommen_amount, ship_amount, pay_amount, pay_time, ship_man_id, ship_man_name, ship_type, ship_time, send_type, order_type, order_status, ship_status, pay_status, is_counting, is_ship, is_receipt, receipt_time, remark from pf_border where
user_id = #{userId,jdbcType=BIGINT}
AND order_status = #{orderStatus,jdbcType=INTEGER}
AND send_type = #{sendType,jdbcType=INTEGER}
ORDER BY id DESC

select id, create_time, create_man, order_code, user_id, user_pid, user_message, supplier_id, modify_time, modify_man, receivable_amount, order_amount, bail_amount, product_amount, recommen_amount, ship_amount, pay_amount, pay_time, ship_man_id, ship_man_name, ship_type, ship_time, send_type, order_type, order_status, ship_status, pay_status, is_counting, is_ship, is_receipt, receipt_time, remark from pf_border where
user_pid = #{userPId,jdbcType=BIGINT}
AND order_status = #{orderStatus,jdbcType=INTEGER}
AND send_type = #{sendType,jdbcType=INTEGER}
ORDER BY id DESC

select pb.id, pb.create_time, pb.order_code, pb.user_id,pb.user_pid,pb.order_amount,pb.order_type,pb.recommen_amount from pf_border pb inner join pf_border_item pbi ON pb.id=pbi.pf_border_id inner join pf_border_recommen_reward pbrr on pbi.id=pbrr.pf_border_item_id
AND pbrr.recommen_user_id = #{userId}
AND pbi.sku_id = #{skuId}
AND pb.recommen_amount >0 AND pb.pay_status=1
ORDER BY pb.id DESC

select pb.id, pb.create_time, pb.order_code, pb.user_id,pbrr.recommen_user_id as user_pid,pb.order_amount,pb.order_type,pb.recommen_amount from pf_border pb inner join pf_border_item pbi ON pb.id=pbi.pf_border_id inner join pf_border_recommen_reward pbrr on pbi.id=pbrr.pf_border_item_id
AND pb.user_pid = #{userId}
AND pbi.sku_id = #{skuId}
AND pb.pay_status=1 AND pb.recommen_amount >0
ORDER BY pb.id DESC

select id, create_time, create_man, order_code, user_id, user_pid, user_message,
supplier_id, modify_time, modify_man, receivable_amount, order_amount, bail_amount,
product_amount, recommen_amount, ship_amount, pay_amount, pay_time, ship_man_id, ship_man_name, ship_type,
ship_time, send_type, order_type, order_status, ship_status, pay_status, is_counting,
is_ship, is_receipt, receipt_time, remark
from
pf_border


        AND id = #{id,jdbcType=BIGINT}


        AND create_time = #{createTime,jdbcType=TIMESTAMP}


        AND create_man = #{createMan,jdbcType=BIGINT}


        AND order_code = #{orderCode,jdbcType=VARCHAR}


        AND user_id = #{userId,jdbcType=BIGINT}


        AND user_pid = #{userPid,jdbcType=BIGINT}


        AND user_message = #{userMessage,jdbcType=VARCHAR}


        AND supplier_id = #{supplierId,jdbcType=INTEGER}


        AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


        AND modify_man = #{modifyMan,jdbcType=BIGINT}


        AND receivable_amount = #{receivableAmount,jdbcType=DECIMAL}


        AND order_amount = #{orderAmount,jdbcType=DECIMAL}


        AND product_amount = #{productAmount,jdbcType=DECIMAL}


        AND recommen_amount = #{recommenAmount,jdbcType=DECIMAL}


        AND ship_amount = #{shipAmount,jdbcType=DECIMAL}


        AND pay_amount = #{payAmount,jdbcType=DECIMAL}


        AND pay_time = #{payTime,jdbcType=TIMESTAMP}


        AND ship_man_id = #{shipManId,jdbcType=INTEGER}


        AND ship_man_name = #{shipManName,jdbcType=VARCHAR}


        AND ship_type = #{shipType,jdbcType=INTEGER}


        AND ship_time = #{shipTime,jdbcType=TIMESTAMP}


        AND order_type = #{orderType,jdbcType=INTEGER}


        AND order_status = #{orderStatus,jdbcType=INTEGER}


        AND ship_status = #{shipStatus,jdbcType=INTEGER}


        AND pay_status = #{payStatus,jdbcType=INTEGER}


        AND is_counting = #{isCounting,jdbcType=INTEGER}


        AND is_ship = #{isShip,jdbcType=INTEGER}


        AND is_receipt = #{isReceipt,jdbcType=INTEGER}


        AND receipt_time = #{receiptTime,jdbcType=TIMESTAMP}


        AND remark = #{remark,jdbcType=VARCHAR}


        AND bail_amount=#{bailAmount,jdbcType=DECIMAL}


        AND send_type=#{sendType,jdbcType=INTEGER}


        AND create_time >=#{beginTime}


        AND create_time <=#{endTime}

select id, create_time, create_man, order_code, user_id, user_pid, user_message,
supplier_id, modify_time, modify_man, receivable_amount, order_amount, bail_amount,
product_amount, recommen_amount, ship_amount, pay_amount, pay_time, ship_man_id, ship_man_name, ship_type,
ship_time, send_type, order_type, order_status, ship_status, pay_status, is_counting,
is_ship, is_receipt, receipt_time, remark
from pf_border


        create_time <= #{expiraTime,jdbcType=TIMESTAMP}


        AND order_status = #{orderStatus,jdbcType=INTEGER}


        AND pay_status = #{payStatus,jdbcType=INTEGER}

select count(id)
from pf_border
where user_pid = #{userPid,jdbcType=BIGINT}

    AND order_status = #{orderStatus,jdbcType=INTEGER}

select id, create_time, create_man, order_code, user_id, user_pid, user_message,
supplier_id, modify_time, modify_man, receivable_amount, order_amount, bail_amount,
product_amount, recommen_amount, ship_amount, pay_amount, pay_time, ship_man_id, ship_man_name, ship_type,
ship_time, send_type, order_type, order_status, ship_status, pay_status, is_counting,
is_ship, is_receipt, receipt_time, remark
FROM pf_border
where
order_type  <>  #{notOrderType,jdbcType=INTEGER}

    AND create_time <= #{expiraTime,jdbcType=TIMESTAMP}


    AND order_status = #{orderStatus,jdbcType=INTEGER}


    AND pay_status = #{payStatus,jdbcType=INTEGER}

select id, create_time, create_man, order_code, user_id, user_pid, user_message,
supplier_id, modify_time, modify_man, receivable_amount, order_amount, bail_amount,
product_amount, recommen_amount, ship_amount, pay_amount, pay_time, ship_man_id, ship_man_name, ship_type,
ship_time, send_type, order_type, order_status, ship_status, pay_status, is_counting,
is_ship, is_receipt, receipt_time, remark
FROM pf_border
where 1=1

    AND order_type = #{orderType,jdbcType=INTEGER}


    AND create_time <= #{expiraTime,jdbcType=TIMESTAMP}


    AND order_status = #{orderStatus,jdbcType=INTEGER}


    AND pay_status = #{payStatus,jdbcType=INTEGER}

mapper.update:
update
    pf_border
SET
    order_status = 2
WHERE
    id = #{orderId,jdbcType=BIGINT}
AND order_status = #{orderStatus,jdbcType=INTEGER}
AND order_type = #{orderType,jdbcType=INTEGER}
AND pay_status = #{payStatus,jdbcType=INTEGER}

mapper.select:
SELECT bo.id, bo.order_code, bo.order_status, bo.order_type, bo.send_type, bo.create_time, bo.receivable_amount, bo.order_amount, bo.pay_amount, bo.bail_amount, bo.pay_time, bi.sku_id, bi.unit_price, bi.quantity, sku.`name`, skui.img_url FROM pf_border bo LEFT JOIN pf_border_item bi ON bi.pf_border_id = bo.id LEFT JOIN com_sku sku ON sku.id = bi.sku_id INNER JOIN com_sku_image skui ON skui.sku_id = bi.sku_id
AND bo.user_id = #{userId}
AND bo.user_pid = #{userPid}
AND bo.order_status = #{orderStatus}
AND bo.order_status IN (0, 9)
ORDER BY bo.create_time DESC, skui.img_url DESC

SELECT pb.id id ,pb.order_code orderCode ,pb.create_time createTime ,pb.user_pid userPid ,pb.order_status orderStatus ,cu.real_name realName ,pbi.sku_name skuName ,cal.name levelName ,pcu.real_name pRealName ,pcal.name pLevelName ,tcu.real_name recommendRealName ,pb.order_amount orderAmount ,pb.recommen_amount recommenAmount ,pb.receivable_amount receivableAmount ,cd.value orderType FROM pf_border pb LEFT JOIN pf_border_item pbi ON pb.id=pbi.pf_border_id LEFT JOIN pf_border_recommen_reward pbrr ON pbi.id=pbrr.pf_border_item_id LEFT JOIN com_agent_level cal ON pbi.agent_level_id=cal.id LEFT JOIN com_user cu ON pb.user_id=cu.id LEFT JOIN com_user pcu ON pb.user_pid=pcu.id LEFT JOIN com_user tcu ON pbrr.recommen_user_id=tcu.id LEFT JOIN pf_user_sku pus ON pb.user_pid=pus.user_id AND pbi.sku_id=pus.sku_id LEFT JOIN com_agent_level pcal ON pus.agent_level_id=pcal.id LEFT JOIN com_dictionary cd ON cd.code='pf_border_type' AND cd.key=pb.order_type WHERE order_status=9
AND pb.order_code = #{orderCode}
AND pb.create_time >= #{startTime}
AND pb.create_time >= #{endTime}
AND cu.real_name LIKE #{realName}
AND pb.order_type = #{orderType}

SELECT bo.id id ,bo.order_code orderCode ,bo.create_time createTime ,bo.order_amount orderAmount ,bo.product_amount productAmount ,bo.bail_amount bailAmount ,bo.receivable_amount receivableAmount ,bo.pay_amount payAmount ,bo.recommen_amount recommenAmount ,bo.order_status orderStatus ,bo.order_type orderType ,bo.send_type sendType ,bo.pay_status payStatus ,bo.ship_status shipStatus ,bo.is_counting isCounting ,pu.real_name puRealName ,ru.real_name ruRealName ,u.real_name uRealName ,bp.pay_type_id payTypeId FROM pf_border bo LEFT JOIN com_user pu ON pu.id = bo.user_pid LEFT JOIN pf_border_recommen_reward brr ON brr.pf_border_id = bo.id LEFT JOIN com_user ru ON ru.id = brr.recommen_user_id LEFT JOIN com_user u ON u.id = bo.user_id LEFT JOIN pf_border_payment bp ON bp.pf_border_id = bo.id AND bp.is_enabled = 1 WHERE bo.order_status = 7
AND EXISTS(SELECT 0 FROM pf_border a INNER JOIN pf_border_item b ON a.id=b.pf_border_id INNER JOIN pf_user_sku_stock c ON a.user_id=c.user_id AND b.sku_id=c.sku_id WHERE a.order_status=7 AND b.quantity <= c.stock AND bo.id=a.id)
AND bo.order_code = #{orderCode}
AND bo.create_time >= #{beginTime}
AND bo.create_time <= #{endTime}
AND bo.order_type = #{orderType}
AND bo.order_status = #{orderStatus}
AND bo.pay_status = #{payStatus}
AND bo.ship_status = #{shipStatus}
AND bo.is_counting = #{isCounting}
AND bp.pay_type_id = #{payTypeId}
