mapper.select:
select id,pay_status from
pf_corder
where
user_id = #{userId,jdbcType=BIGINT}
AND  sku_id = #{skuId,jdbcType=INTEGER}
AND order_type=#{orderType,jdbcType=INTEGER}

select id, create_time, create_man, order_code, order_type, sku_id, user_id, user_pid, user_massage, supplier_id, modify_time, modify_man, receivable_amount, order_amount, product_amount, ship_amount, pay_amount, pay_time, ship_man_id, ship_man_name, delivery_time, ship_remark, replace_order_id, order_status, ship_status, pay_status, is_ship, is_replace, is_receipt, receipt_time, remark from pf_corder
AND id = #{id}
AND order_code = #{orderCode}
AND create_man = #{createMan}
AND ship_status = #{shipStatus}
AND order_status = #{orderStatus}
AND pay_status = #{payStatus}
AND create_time >=#{beginTime}
AND create_time <=#{endTime}

SELECT co.id id ,co.order_code orderCode ,co.create_time createTime ,co.order_amount orderAmount ,co.product_amount productAmount ,co.receivable_amount receivableAmount ,co.pay_amount payAmount ,co.order_status orderStatus ,co.order_type orderType ,co.pay_status payStatus ,co.ship_status shipStatus ,co.user_massage userMessage ,pu.real_name puRealName ,u.wx_nk_name uWxNkName ,bp.pay_type_id payTypeId ,cc.consignee consignee ,cc.mobile mobile ,cc.province_name provinceName ,cc.city_name cityName ,cc.region_name regionName ,cc.address address ,cc.zip zip FROM pf_corder co LEFT JOIN com_user pu ON pu.id = co.user_pid LEFT JOIN com_user u ON u.id = co.user_id LEFT JOIN pf_corder_payment bp ON bp.pf_corder_id = co.id AND bp.is_enabled = 1 LEFT JOIN pf_corder_consignee cc ON cc.pf_corder_id = co.id WHERE co.order_status = 1
AND co.order_code = #{orderCode}
AND co.create_time >= #{beginTime}
AND co.create_time <= #{endTime}
AND co.order_type = #{orderType}
AND co.order_status = #{orderStatus}
AND co.pay_status = #{payStatus}
AND co.ship_status = #{shipStatus}
AND co.is_counting = #{isCounting}
AND bp.pay_type_id = #{payTypeId}
