mapper.select:
select MAX(bail)
from pf_sku_agent
where sku_id= #{skuId,jdbcType=INTEGER};

select  MIN(bail)
from pf_sku_agent
where sku_id= #{skuId,jdbcType=INTEGER};

select id, sku_id, agent_level_id, icon, unit_price, quantity, total_price, bail,
back_img, is_show, remark, is_upgrade, reward_unit_price,freeman_upper_num, supplement_lower_quantity
from pf_sku_agent
where sku_id = #{skuId}
and agent_level_id =#{levelId}

select id, sku_id, agent_level_id, icon, unit_price, quantity, total_price, bail,
back_img, is_show, remark, is_upgrade, reward_unit_price,freeman_upper_num, supplement_lower_quantity
from pf_sku_agent
where sku_id = #{skuId}
and agent_level_id =#{levelId}
and is_show =#{isShow}

SELECT
    *
FROM
    pf_sku_agent a
WHERE
    a.agent_level_id >= #{agentLevelId,jdbcType=INTEGER}
AND a.sku_id = #{skuId,jdbcType=INTEGER}
AND a.is_show = 1;
