mapper.select:
SET @begin_date= #{beginTime};
SET @end_date=DATE_ADD(@begin_date,INTERVAL 1 DAY);
SELECT CASE appid
WHEN #{appid}
THEN '麦链商城' ELSE '麦链合伙人'
END 'registerCountUser',COUNT(0) registerCount FROM com_wx_user WHERE create_time <= @end_date GROUP BY appid

SET @begin_date= #{beginTime};
SET @end_date=DATE_ADD(@begin_date,INTERVAL 1 DAY);
SELECT CASE appid WHEN #{appid} THEN '麦链商城' ELSE '麦链合伙人' END 'registerCountUser',COUNT(0) registerCount  FROM com_wx_user WHERE create_time >=@begin_date AND create_time <= @end_date GROUP BY appid;

SET @begin_date=#{beginTime};
SET @end_date=DATE_ADD(@begin_date,INTERVAL 1 DAY);
SELECT COUNT(0) 'registerCount' FROM pf_user_sku WHERE create_time <= @end_date;

SET @begin_date=#{beginTime};
SET @end_date=DATE_ADD(@begin_date,INTERVAL 1 DAY);
 SELECT COUNT(0) 'registerCount' FROM pf_user_sku WHERE create_time>=@begin_date AND create_time <= @end_date

SET @begin_date=#{beginTime};
SET @end_date=DATE_ADD(@begin_date,INTERVAL 1 DAY);
SELECT COUNT(DISTINCT a.user_id) 'registerCount' FROM sf_order a
INNER JOIN sf_order_item b ON a.id=b.sf_order_id
WHERE a.pay_status=1 AND a.pay_time <= @end_date;

SET @begin_date=#{beginTime};
SET @end_date=DATE_ADD(@begin_date,INTERVAL 1 DAY);
SELECT COUNT(DISTINCT a.user_id) 'registerCount' FROM sf_order a
INNER JOIN sf_order_item b ON a.id=b.sf_order_id
WHERE a.pay_status=1 AND a.pay_time > @begin_date AND a.pay_time <= @end_date;
