mapper.update:
update com_user
set mobile = #{mobile,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select real_name
from com_user
where id = #{pid}

select id, create_time, mobile, user_mail, wx_head_img, wx_nk_name, sex, real_name,
id_card, id_card_front_url, id_card_back_url, wx_id, send_type, audit_reason, audit_status,
audit_date, wx_unionid, password, pwd_salt, register_source, app_token, app_token_expire,
user_key, is_binding, is_agent, is_buy
from
com_user


        AND id = #{id}


        AND create_time = #{createTime}


        AND wx_nk_name = #{wxNkName}


        AND wx_id = #{wxId}


        AND real_name = #{realName}


        AND mobile = #{mobile}


        AND id_card = #{idCard}


        AND audit_status = #{auditStatus}


        AND is_buy = #{isBuy,jdbcType=INTEGER}

select id, create_time, mobile, user_mail, wx_head_img, wx_nk_name, sex, real_name,
id_card, id_card_front_url, id_card_back_url, wx_id, send_type, audit_reason, audit_status,
audit_date, wx_unionid, password, pwd_salt, register_source, app_token, app_token_expire,
user_key, is_binding, is_agent, is_buy
from
com_user


        AND id = #{id}


        AND create_time = #{createTime}


        AND wx_nk_name = #{wxNkName}


        AND wx_id = #{wxId}


        AND real_name = #{realName}


        AND mobile = #{mobile}


        AND id_card = #{idCard}


        AND audit_status = #{auditStatus}


        AND is_buy = #{isBuy,jdbcType=INTEGER}

select id, create_time, mobile, user_mail, wx_head_img, wx_nk_name, sex, real_name,
id_card, id_card_front_url, id_card_back_url, wx_id, send_type, audit_reason, audit_status,
audit_date, wx_unionid, password, pwd_salt, register_source, app_token, app_token_expire,
user_key, is_binding, is_agent, is_buy
from
com_user


        AND id = #{id}


        AND create_time = #{createTime}


        AND wx_nk_name = #{wxNkName}


        AND wx_id = #{wxId}


        AND real_name = #{realName}


        AND mobile = #{mobile}


        AND id_card = #{idCard}

    AND audit_status IN (1,3)

select id, create_time, mobile, user_mail, wx_head_img, wx_nk_name, sex, real_name,
id_card, id_card_front_url, id_card_back_url, wx_id, send_type, audit_reason, audit_status,
audit_date, wx_unionid, password, pwd_salt, register_source, app_token, app_token_expire,
user_key, is_binding, is_agent, is_buy
from
com_user


        AND id = #{id}


        AND create_time = #{createTime}


        AND wx_nk_name = #{wxNkName}


        AND wx_id = #{wxId}


        AND real_name = #{realName}


        AND mobile = #{mobile}


        AND id_card = #{idCard}

    AND audit_status IN (1,3)

select id, create_time, mobile, user_mail, wx_head_img, wx_nk_name, sex, real_name, id_card, id_card_front_url, id_card_back_url, wx_id, send_type, audit_reason, audit_status, audit_date, wx_unionid, password, pwd_salt, register_source, app_token, app_token_expire, user_key, is_binding, is_agent, is_buy from com_user
AND real_name LIKE #{realName}
AND mobile = #{mobile}
