mapper.select:
SELECT COUNT(a.id) as num,IFNULL(SUM(b.income_fee),0) as sam  FROM pf_user_sku a
LEFT JOIN pf_user_statistics b ON a.user_id=b.user_id AND a.sku_id=b.sku_id
WHERE a.tree_code LIKE CONCAT(#{treeCode},'%');

SELECT SUM(b.down_order_count) as orders FROM pf_user_sku a
INNER JOIN pf_user_statistics b ON a.user_id=b.user_id AND a.sku_id=b.sku_id
WHERE a.tree_code LIKE CONCAT(#{treeCode},'%');
