mapper.select:
select
       id,
       create_time,
       pid,
       user_id,
       user_pid,
       sku_id,
       pf_border_id,
       tree_code,
       tree_level,
       remark
from
       pf_user_recommen_relation


        AND id = #{id}


        AND create_time = #{createTime}


        AND pid = #{pid}


        AND user_id = #{userId}


        AND user_pid = #{userPid}


        AND sku_id = #{skuId}


        AND pf_border_id = #{pfBorderId}


        AND tree_code = #{treeCode}


        AND tree_level = #{treeLevel}


        AND remark = #{remark}
