mapper.select:
select id, create_time, sku_id, user_id, user_pid, is_enable, remark
from pf_user_relation
where id = #{id,jdbcType=BIGINT}

select id, create_time, sku_id, user_id, user_pid, is_enable, remark
from pf_user_relation
where user_id = #{userId}
and sku_id=#{skuId}
and is_enable=1

select id, create_time, sku_id, user_id, user_pid, is_enable, remark
from pf_user_relation
WHERE user_id = #{userId,jdbcType=BIGINT}
AND sku_id = #{skuId,jdbcType=INTEGER}
AND user_pid = #{pUserId,jdbcType=BIGINT}

mapper.update:
update pf_user_relation
set is_enable = 0
where user_id = #{userId,jdbcType=BIGINT}
AND sku_id = #{skuId,jdbcType=INTEGER}

mapper.select:
select
       id,
       create_time,
       sku_id,
       user_id,
       user_pid,
       is_enable,
       remark
from
       pf_user_relation


        AND id = #{id}


        AND create_time = #{createTime}


        AND sku_id = #{skuId}


        AND user_id = #{userId}


        AND user_pid = #{userPid}


        AND is_enable = #{isEnable}


        AND remark = #{remark}
