mapper.select:
select id, create_time, code, pid, user_id, user_pid, sku_id, agent_level_id, is_pay,
is_certificate, pf_border_id, bail, agent_num,remark
from pf_user_sku
where pf_border_id = #{bOrderId}
and user_id = #{userId}
and sku_id = #{skuId}

select
id,
create_time,
code,
pid,
user_id,
user_pid,
sku_id,
agent_level_id,
is_pay,
is_certificate,
pf_border_id,
bail,
agent_num,
remark
FROM
pf_user_sku


        AND id = #{id}


        AND code = #{code}


        AND pid = #{pid}


        AND user_id = #{userId}


        AND sku_id = #{skuId}


        AND agent_level_id = #{agentLevelId}


        AND is_pay = #{isPay}


        AND is_certificate = #{isCertificate}


        AND pf_border_id = #{pfBorderId}


        AND remark = #{remark}


        and bail = #{bail,jdbcType=DECIMAL}


        agent_num = #{agentNum,jdbcType=BIGINT},


        and user_pid = #{userPid,jdbcType=BIGINT}

select
id,
create_time,
code,
pid,
user_id,
user_pid,
sku_id,
agent_level_id,
is_pay,
is_certificate,
pf_border_id,
bail,
agent_num,
remark
FROM
pf_user_sku


        AND id = #{id}


        AND code = #{code}


        AND pid = #{pid}


        AND user_id = #{userId}


        AND sku_id = #{skuId}


        AND agent_level_id = #{agentLevelId}


        AND is_pay = #{isPay}


        AND is_certificate = #{isCertificate}


        AND pf_border_id = #{pfBorderId}


        AND remark = #{remark}


        and bail = #{bail,jdbcType=DECIMAL}


        agent_num = #{agentNum,jdbcType=BIGINT},


        and user_pid = #{userPid,jdbcType=BIGINT}

SELECT COUNT(0) FROM pf_user_sku
WHERE is_pay=1
AND is_certificate=1
AND user_id=#{userId}
AND sku_id!=#{skuId}
