mapper.select:
select SUM(recommen_get_fee) as getFee, SUM(recommen_send_fee) as sendFee
from pf_user_statistics
where user_id = #{userId}

select id, create_time, user_id,sku_id, income_fee, profit_fee, cost_fee, up_order_count,
up_product_count, down_order_count, down_product_count, take_order_count, take_product_count,
take_fee, version, remark, recommen_get_fee, recommen_send_fee
from pf_user_statistics
where user_id = #{userId,jdbcType=BIGINT}
AND sku_id = #{skuId,jdbcType=INTEGER}

SELECT pus.`code` `code`, sku.NAME skuName, u.real_name realName, ust.recommen_get_fee recommenGetFee, ust.recommen_send_fee recommenSendFee, ( SELECT COUNT(0) countPresentee FROM pf_user_recommen_relation WHERE user_id = pus.user_id AND sku_id = pus.sku_id AND user_pid != 0 ), ( SELECT COUNT(0) countRecommend FROM pf_user_recommen_relation WHERE user_pid = pus.user_id AND sku_id = pus.sku_id ) FROM pf_user_sku pus INNER JOIN com_sku sku ON pus.sku_id = sku.id INNER JOIN com_user u ON pus.user_id = u.id INNER JOIN pf_user_statistics ust ON pus.user_id = ust.user_id AND pus.sku_id = ust.sku_id
AND pus.code = #{code}
AND u.real_name = #{realName}
