mapper.insert:
insert into pf_user_upgrade_notice (id, create_time, code, 
    user_id, user_pid, sku_id, 
    org_agent_level_id, wish_agent_level_id, status, 
    pf_border_id, remark, up_status
    )
values (#{id,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{code,jdbcType=VARCHAR}, 
    #{userId,jdbcType=BIGINT}, #{userPid,jdbcType=BIGINT}, #{skuId,jdbcType=INTEGER}, 
    #{orgAgentLevelId,jdbcType=INTEGER}, #{wishAgentLevelId,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, 
    #{pfBorderId,jdbcType=BIGINT}, #{remark,jdbcType=VARCHAR}, #{upStatus,jdbcType=INTEGER}
    )

mapper.select:
select id, create_time, code, user_id, user_pid, sku_id, org_agent_level_id, wish_agent_level_id,
status, pf_border_id, remark, up_status
from pf_user_upgrade_notice


        AND create_time >=#{beginTime}


        AND create_time <=#{endTime}


        AND code = #{code}


        AND status = #{status}

SELECT uun.create_time createTime, org_al.name orgLevelName, wish_al.name wishLevelName, p_user.real_name pUserName, r_user.real_name recommendUserName FROM pf_user_upgrade_notice uun LEFT JOIN com_agent_level org_al ON org_al.id = uun.org_agent_level_id LEFT JOIN com_agent_level wish_al ON wish_al.id = uun.wish_agent_level_id LEFT JOIN com_user p_user ON p_user.id = uun.user_pid LEFT JOIN pf_user_recommen_relation urr ON urr.user_id = uun.user_id AND urr.sku_id = uun.sku_id LEFT JOIN com_user r_user ON r_user.id = urr.user_pid
AND uun.user_id = #{userId}
AND uun.sku_id = #{skuId}
