mapper.insert:
insert into com_sku (id, name, spu_id, 
    create_time, create_man, modify_time, 
    modify_man, bar_code, price_cost, 
    price_market, price_retail, remark
    )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{spuId,jdbcType=INTEGER}, 
    #{createTime,jdbcType=TIMESTAMP}, #{createMan,jdbcType=BIGINT}, #{modifyTime,jdbcType=TIMESTAMP}, 
    #{modifyMan,jdbcType=BIGINT}, #{barCode,jdbcType=VARCHAR}, #{priceCost,jdbcType=DECIMAL}, 
    #{priceMarket,jdbcType=DECIMAL}, #{priceRetail,jdbcType=DECIMAL}, #{remark,jdbcType=VARCHAR}
    )

mapper.select:
select name
from com_sku
where id = #{skuId,jdbcType=INTEGER}
