mapper.insert:
insert into com_user_account (id, com_user_id, total_income_fee,cost_fee,
    extractable_fee, counting_fee, bail_fee,
    created_time, changed_time, changed_by
    )
values (#{id,jdbcType=BIGINT}, #{comUserId,jdbcType=BIGINT}, #{totalIncomeFee,jdbcType=DECIMAL},#{costFee,jdbcType=DECIMAL},
    #{extractableFee,jdbcType=DECIMAL}, #{countingFee,jdbcType=DECIMAL}, #{bailFee,jdbcType=DECIMAL},
    #{createdTime,jdbcType=TIMESTAMP}, #{changedTime,jdbcType=TIMESTAMP}, #{changedBy,jdbcType=VARCHAR}
    )

mapper.update:
update com_user_account
set total_income_fee = totalIncomeFee+#{orderPayAmount},
counting_fee = counting_fee+#{orderPayAmount}
where com_user_id = #{comUserId}

update com_user_account
set total_income_fee = total_income_fee + #{billAmount,jdbcType=DECIMAL},
    extractable_fee = extractable_fee + #{billAmount,jdbcType=DECIMAL}
WHERE com_user_id = #{userid,jdbcType=DECIMAL}
