mapper.update:
update com_user
set mobile = #{mobile,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select real_name
from com_user
where id = #{pid}

select id, create_time, openid, wx_nk_name, wx_id, wx_head_img, access_token, refresh_token, atoken_expire, rtoken_expire, real_name, mobile, id_card, id_card_front_url, id_card_back_url, province_id, province_name, city_id, city_name, region_id, region_name, address, is_agent, sex, send_type, audit_status, audit_reason from com_user
AND id = #{id}
AND create_time = #{createTime}
AND wx_nk_name = #{wxNkName}
AND wx_id = #{wxId}
AND real_name = #{realName}
AND mobile = #{mobile}
AND id_card = #{idCard}
AND audit_status = #{auditStatus}

select id, create_time, openid, wx_nk_name, wx_id, wx_head_img, access_token, refresh_token, atoken_expire, rtoken_expire, real_name, mobile, id_card, id_card_front_url, id_card_back_url, province_id, province_name, city_id, city_name, region_id, region_name, address, is_agent, sex, send_type, audit_status, audit_reason from com_user
AND id = #{id}
AND create_time = #{createTime}
AND wx_nk_name = #{wxNkName}
AND wx_id = #{wxId}
AND real_name = #{realName}
AND mobile = #{mobile}
AND id_card = #{idCard}
AND audit_status IN (1,3)
