mapper.select:
select id, create_time, code, pid, user_id, sku_id, agent_level_id, is_pay, is_certificate,
pf_border_id, remark
from pf_user_sku
where user_id = #{userId}
and sku_id = #{skuId}

SELECT
        id,
        create_time,
        code,
        pid,
        user_id,
        sku_id,
        agent_level_id,
        is_pay,
        is_certificate,
        pf_border_id,
        remark
FROM
      pf_user_sku


        AND id = #{id}


        AND code = #{code}


        AND pid = #{pid}


        AND user_id = #{userId}


        AND sku_id = #{skuId}


        AND agent_level_id = #{agentLevelId}


        AND is_pay = #{isPay}


        AND is_certificate = #{isCertificate}


        AND pf_border_id = #{pfBorderId}


        AND remark = #{remark}
